DROP TRIGGER IF EXISTS `puan_guncelle`;
DELIMITER $$
CREATE TRIGGER `puan_guncelle` AFTER INSERT ON `sevkiyat_hareketleri` FOR EACH ROW BEGIN
    DECLARE toplam_is INT;
    DECLARE zamaninda_sayisi INT;
    DECLARE gecikme_sayisi INT;
    DECLARE hasar_sayisi INT;
    DECLARE yeni_basari INT;
    DECLARE yeni_risk INT;

   
    SELECT COUNT(*), 
           SUM(CASE WHEN durum = 'Zamanında' THEN 1 ELSE 0 END),
           SUM(CASE WHEN durum = 'Gecikti' THEN 1 ELSE 0 END),
           SUM(CASE WHEN durum = 'Hasarlı' THEN 1 ELSE 0 END)
    INTO toplam_is, zamaninda_sayisi, gecikme_sayisi, hasar_sayisi
    FROM sevkiyat_hareketleri
    WHERE personel_id = NEW.personel_id;

   
    IF toplam_is > 0 THEN
        SET yeni_basari = (zamaninda_sayisi / toplam_is) * 100;
        
        
        SET yeni_risk = LEAST(10, (hasar_sayisi * 3) + (gecikme_sayisi * 1));
    ELSE
        SET yeni_basari = 100; 
        SET yeni_risk = 0;
    END IF;

  
    UPDATE lojistik_personel 
    SET basari_orani = yeni_basari, 
        risk_puani = yeni_risk 
    WHERE id = NEW.personel_id;

END
$$
DELIMITER ;

